/**
 * Create hero text as a background plane in the 3D scene
 */
createHeroText() {
    const heroText = "HOUSE";
    const canvasWidth = 2048;
    const canvasHeight = 512;

    // Create off-screen canvas for text rendering
    this.heroTextCanvas = document.createElement('canvas');
    this.heroTextCanvas.width = canvasWidth;
    this.heroTextCanvas.height = canvasHeight;
    const ctx = this.heroTextCanvas.getContext('2d');

    // Create texture from canvas
    this.heroTextTexture = new THREE.CanvasTexture(this.heroTextCanvas);
    this.heroTextTexture.minFilter = THREE.LinearFilter;
    this.heroTextTexture.magFilter = THREE.LinearFilter;

    // Create plane geometry - make it large to fill the background
    const planeWidth = 20;
    const planeHeight = 5;
    const geometry = new THREE.PlaneGeometry(planeWidth, planeHeight);

    // Create material with the text texture
    const material = new THREE.MeshBasicMaterial({
        map: this.heroTextTexture,
        transparent: true,
        opacity: 1.0,
        depthWrite: false,
        side: THREE.DoubleSide
    });

    this.heroTextPlane = new THREE.Mesh(geometry, material);

    // Position it behind the images
    this.heroTextPlane.position.set(0, 0, -5);

    this.scene.add(this.heroTextPlane);

    // Initial text render
    this.updateHeroTextCanvas(ctx, heroText, canvasWidth, canvasHeight);

    console.log('🎨 Hero text created in 3D scene');
}

